import React, { useState, useEffect } from "react";

function Todolist() {
  const [todoList, setTodo] = useState(["sandeep"]);
  const [todoname, setName] = useState("");

  useEffect(() => {
    const gettodo = JSON.parse(localStorage.getItem("todo"));
    if (gettodo) {
      setTodo(gettodo);
    }
  }, []);

  useEffect(() => {
    setTodo(localStorage.setItem("todo", JSON.stringify(todoList)));
  }, [todoist]);

  const setTodoName = (e) => {
    setName(e.target.value);
  };

  const addTodoButton = () => {
    setTodo([...todoList, todoname]);
  };

  const delateTodo = (index) => {
    const f = todoList.filter((_, i) => index !== i);
    setTodo(f);
  };

  return (
    <div>
      <h1>todoList</h1>
      <input
        onChange={setTodoName}
        value={todoname}
        placeholder="addtodo"
      ></input>
      <button onClick={addTodoButton}>add</button>
      <ul>
        {todoList.map((todo, index) => (
          <i key={index}>
            <p> {todo} </p>
            <button onClick={() => delateTodo(index)}> delate</button>
          </i>
        ))}
      </ul>
    </div>
  );
}
export default Todolist;
